chrome.runtime.onMessage.addListener((e,l,t)=>{e.action==="open-in-another-profile"&&e.url&&chrome.storage.sync.get(["profile","target","custom"],r=>{if(r.target==="profile"){const o=r.profile||"Profile 2";chrome.runtime.sendNativeMessage("com.kaishoya.openlinkprofile",{url:e.url,profile:o},function(i){})}else r.target==="_blank"?chrome.tabs.create({url:e.url}):r.target==="_self"?chrome.tabs.update({url:e.url}):chrome.tabs.create({url:e.url})})});
